mapper.select:
select
  count(*)
from 
  party p
  join region r on p.rno = r.rno
  join sports s on p.sno = s.sno
where
  act = 1

  and p.rno = #{gu}


  and s.sno = #{sports}


  and p.pdate = #{partyDate}


  and p.ptime = #{partyTime}


  and p.title like concat("%", #{searchText}, "%")

select p.pno, r.sido, r.sigu, s.name, p.title, p.cdt, p.headcnt, p.ptime, p.pdate, (select truncate(avg(star), 1) from party_review where pno=p.pno) as star, r.rno, s.sno, p.thumbnail from party p join region r on p.rno = r.rno join sports s on p.sno = s.sno where p.act = 1
and p.rno = #{gu}
and s.sno = #{sports}
and p.pdate = #{partyDate}
and p.ptime = #{partyTime}
and p.title like concat("%", #{searchText}, "%")
order by
star desc
,
p.cdt desc
,
p.pdate desc
p.pno desc
limit #{pagesStart}, #{perPageNum}
